import React from 'react'
import styled from 'styled-components'


const Container = styled.div`
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  flex-wrap: wrap;
  color: white;
  background-color: black;
  width: 100%;
  margin-top: 5%;
  height: 40%;
  //for Mobiles
  @media only screen and (max-width: 600px) {
    width: 100%;
  }
  //for Tablets and Medium Screens
  @media only screen and (min-width: 600px) {
    height: 20%;
  }
  //for laptops and desktops
  @media only screen and (min-width: 992px) {
    height: 30%;
  }
`;

const Title = styled.div`
  color: white;
  font-weight: bold;
  font-size: 20px;
  padding: 2%;
`;
const Description = styled.div`
  color: gray;
  display: flex;
  flex-direction: column;
  align-items: center;
  font-weight: bold;
  font-size: 20px;
  width: 30%;
  //for Mobiles
  @media only screen and (max-width: 600px) {
    width: 100%;
    text-align: center;
    font-size: 15px;
    padding: 2%;
  }
  //for Tablets and Medium Screens
  @media only screen and (min-width: 600px) {
    width: 40%;
    font-size: 18px;
    text-align: center;
  }
  //for laptops and desktops
  @media only screen and (min-width: 992px) {
    width: 30%;
    font-size: 20px;
    text-align: center;
  }
`;

const Menu = styled.div`
  display: flex;
  flex-direction: column;
  list-style: none;
`;
const MenuItem = styled.li`
  margin-top: 10px;
  font-size: 20px;
  font-weight: bold;
  margin-right: 30px;
  color: ${(props) => props.theme.colors.textDark};
  cursor: pointer;
  &:hover {
    color: white;
  }
`;

const MenuItems = styled.div`
  margin-top: 10px;
  font-size: 20px;
  font-weight: bold;
  color: gray;
  cursor: pointer;
`;
const Footer = () => {
  return (
    <Container>
      <Description>
      <Title>About Us</Title>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Iste quae illo, officia dolores ab quo ut porro commodi. Recusandae possimus autem dignissimos neque adipisci. Nihil.
      </Description>
        <MenuItems>
      <Menu>
      <Title>Our Services</Title>
        <MenuItem href="#">Training Courses</MenuItem>
        <MenuItem href="#">Services Desk</MenuItem>
        <MenuItem href="#">Proative Services</MenuItem>
        <MenuItem href="#">User Support</MenuItem>
        <MenuItem href="#">28/10 Services</MenuItem>
      </Menu>
      </MenuItems>
      <MenuItems>
      <Title>Our Polices</Title>
      <Menu>
        <MenuItem>Privacy Policy</MenuItem>
        <MenuItem>Data Policy</MenuItem>
      </Menu>
      </MenuItems>
    </Container>
  )
}

export default Footer
